					Calamity.GetItem("WyvernsCall")?.DisplayName.AddTranslation(GameCulture.French, "Appel de la Wyverne");
					Calamity.GetItem("WyvernsCall")?.Tooltip.AddTranslation(GameCulture.French, "Je fais appel à la mythique wyverne pour inonder les terres de sa grace\nTire des wyvernes et des plumes colorées depuis le ciel qui se collent aux ennemis et aux tuiles avant d'exploser");
					Calamity.GetItem("PlagueCaller")?.DisplayName.AddTranslation(GameCulture.French, "Appeleur de Peste");
