					Calamity.GetItem("CosmiliteBasin")?.DisplayName.AddTranslation(GameCulture.French, "Bassin en Cosmilite");
					Calamity.GetItem("WulfrumBattery")?.DisplayName.AddTranslation(GameCulture.French, "Batterie Wulfrum");
