					Calamity.GetItem("EnchantedAxe").DisplayName.AddTranslation(GameCulture.French, "Hache Enchantée");
					Calamity.GetItem("EnchantedAxe").Tooltip.AddTranslation(GameCulture.French, "Lance une hache rapide qui revient après une courte distance ou avoir percuté un mur\nAu point culminant de sa trajectoire, une hache magique qui traverse les murs sera envoyée sur l'ennemi le plus proche\nLes attaques furtives font aller la hache plus loin, tout en lancant une myriade des haches additionnelles dans toutes les directions");
					Calamity.GetItem("Affliction").DisplayName.AddTranslation(GameCulture.French, "Affliction");
					Calamity.GetItem("Affliction").Tooltip.AddTranslation(GameCulture.French, "Donne à vous et à votre équipe +1 régénération de vie,\n+10% de vie maximum, 7% de réduction des dégâts, 20 défense, et 10% de dégâts supplémentaires");
					Calamity.GetItem("Navyplate").DisplayName.AddTranslation(GameCulture.French, "Plaque Azur");
